mapper.update:
UPDATE MOVIE
CONFIRM = #{confirm}, REFUSE_RS = #{refuseRs},
WHERE MV_NO = #{mvNo}

UPDATE PERFORMANCE
CONFIRM = #{confirm}, REFUSE_RS = #{refuseRs},
WHERE P_NO = #{pNo}

UPDATE GOODS
CONFIRM = #{confirm}, REFUSE_RS = #{refuseRs},
WHERE G_NO = #{gNo}

mapper.select:
SELECT * FROM ( SELECT ROWNUM RN , A.* FROM ( SELECT * FROM MOVIE WHERE
(NAME LIKE '%'||#{searchName}||'%' OR GENRE LIKE '%'||#{searchName}||'%' OR CONFIRM LIKE '%'||#{searchName}||'%') NAME LIKE '%'||#{searchName}||'%' GENRE LIKE '%'||#{searchName}||'%' CONFIRM LIKE '%'||#{searchName}||'%' 1 = 1
ORDER BY G_NO DESC ) A ) WHERE RN > (#{pageNum}-1) * #{amount} AND RN <= #{pageNum} * #{amount}
