mapper.select:
SELECT P_NO,NAME,CONTENT,C_ID,ACTOR,FILE_CD,LOC,ADDR,PRICE FROM ( SELECT ROWNUM RN,A.* FROM ( SELECT P_NO,NAME,CONTENT,C_ID,ACTOR,FILE_CD,LOC,ADDR,PRICE FROM PERFORMANCE WHERE
(NAME LIKE '%'||#{searchName}||'%' OR ACTOR LIKE '%'||#{searchName}||'%' OR LOC LIKE '%'||#{searchName}||'%') NAME LIKE '%'||#{searchName}||'%' ACTOR LIKE '%'||#{searchName}||'%' LOC LIKE '%'||#{searchName}||'%' 1 = 1
ORDER BY P_NO DESC ) A ) WHERE rn BETWEEN (#{pageNum} -1) *#{amount} AND #{pageNum} * #{amount}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM (
SELECT P.P_NO,P.NAME,P.CONTENT,P.ACTOR,P.FILE_CD,P.LOC,P.ADDR,P.PRICE,P.LAT,P.LNG, PS.FRDT,PS.TRDT,PS.TIME,PS.ENDTIME FROM PERFORMANCE P JOIN PERFORMANCE_SCHEDULE PS ON P.P_NO = PS.P_NO
AND 1=1
AND PS.FRDT = #{frDt}
AND P.NAME LIKE '%' || #{name} || '%'
AND P.LOC LIKE '%' || #{loc} || '%'
) A ) WHERE RN > (#{pageNum}-1) * #{amount} AND RN <= #{pageNum} * #{amount}
